    1px 1px 16px -2px hsl(0 63% 18% / 0.12),
    1px 1px 2px -3px hsl(0 63% 18% / 0.12);
  --shadow-md:
    1px 1px 16px -2px hsl(0 63% 18% / 0.12),
    1px 2px 4px -3px hsl(0 63% 18% / 0.12);
  --shadow-lg:
    1px 1px 16px -2px hsl(0 63% 18% / 0.12),
