  --sidebar-border: oklch(0.87 0 0);
  --sidebar-ring: oklch(0.65 0.15 150.31);
  --font-sans: Plus Jakarta Sans, sans-serif;
  --font-serif: Source Serif 4, serif;
  --font-mono: JetBrains Mono, monospace;
  --shadow-color: hsl(0 0% 0%);
