  --secondary: oklch(0.83 0.08 74.44);
  --secondary-foreground: oklch(0.44 0.01 73.64);
  --muted: oklch(0.94 0.02 83.26);
  --muted-foreground: oklch(0.55 0.01 58.07);
  --accent: oklch(0.9 0.05 74.99);
