  --sidebar-accent: oklch(0.22 0.012 250);
  --sidebar-accent-foreground: oklch(0.80 0.01 250);
  --sidebar-border: oklch(0.30 0.01 250);
  --sidebar-ring: oklch(0.57 0.18 260);
